INSERT INTO `tbl_problem_solving` (`id`, `kode_error`, `deskripsi_error`, `file`, `solusi_error`) VALUES
	(13, 'User-defined type not defined', 'Aplikasi belum terkoneksi dengan database', '20190524160818', 'Cek jaringan komputer '),
	(14, 'Run-time error ‘-2147217904(80004005)’ : [Microsoft][ODBC driver Manager] Data source name too long', 'Salah penulisan pada kata Provider pada koneksi.\r\nContoh : menulisnya Profider.', '2019061785145', 'Perbaiki penulisan kata Provider.'),
	(15, 'Run-time error ‘3706’ : Provider cannot be found, it may not be properly installed', 'Salah penulisan pada koneksi. \r\nPada bagian : Microsoft.jet.OLEDB.4.0;data source = “', '2019061785228', 'Perbaiki penulisan pada bagian Microsoft.jet.OLEDB.4.0;data source = “'),
	(16, 'Run-time error ‘-2147467259(80004005)’ : Could not find file ‘D:\\Inventory.mdb', 'Ada dua kemungkinan penyebab error tersebut.\r\nPertama mungkin Anda salah menuliskan nama databasenya,\r\nKedua mungkin Anda belum menyimpan databasenya satu folder dengan file VB 6.0', '2019061785501', 'Jika Penyebab pertama : Perbaiki penulisan nama databasenya.\r\nJika Penyebab kedua : Simpan file database access satu folder dengan File-file Ms VB 6.0'),
	(17, 'Run-time error ‘-2147467259(80004005)’ : Could not find installable ISAM', 'Kesalahan penulisan pada bagian : `\\Inventory.mdb;persist security info=false`\r\nContohnya : `\\Inventory.mdb;perssist security info=false`\r\nArti tulisan berwarna biru : Tulisan tersebut merupakan nama database. Sesuaikan nama database dengan yang Anda buat.', '2019061785541', 'Perbaiki penulisan pada bagian : `\\Inventory.mdb;persist security info=false`.'),
	(18, 'Run-time error ‘91’ : Object variable or with block variable not set', 'Anda belum menuliskan :\r\nSet db = New ADODB.Connection\r\nKet : Tulisan berwarna merah adalah sebuah variable yang bersifat optional. Karena itu sesuaikan dengan variable yang Anda buat.', '2019061785727', 'Tuliskan : Set db = New ADODB.Connection Dibawah koneksi dengan provider'),
	(19, 'Run-time error ‘3709’ : The connection cannot be used to perform this operation. It is either closed or invalid in this context.', 'Ada beberapa kemungkinan sebabnya\r\nPertama : Anda belum menuliskan Db.open koneksi\r\nKedua : Anda belum membuat koneksi dengan Ms Access\r\nKet : Tulisan berwarna merah adalah sebuah variable yang bersifat optional. Karena itu sesuaikan dengan variable yang Anda buat.', '2019061785835', 'Tuliskan tulisan berikut ini diatas end sub:\r\nDb.open koneksi Atau Buat koneksi'),
	(20, 'Run-time error ‘-2147217900(80040e14)’ : Invalid SQL statement; expected ‘Delete’, ‘Insert’, ‘Procedure’, ’Select’, or ‘update’', 'Salah penulisan pada perintah SQL. Contohnya Insert menjadi Inser.', '2019061790019', 'Periksa kembali perintah SQL dan perbaiki.'),
	(21, 'Run-time error ‘-2147467259(80004005)’ : Number of Query values and destination fields are not the same', 'Jumlah data yang diinputkan tidak sama dengan jumlah field yang ada pada table', '2019061790050', 'Periksa jumlah fields pada table bersangkutan, tipe data, dan inputkan data sesuai dengan fields pada table.'),
	(22, 'Run-time error ‘-2147217904(80040e10)’ : No value given for one or more required parameters.', 'Field yang dituliskan pada perintah SQL update, tidak sama dengan field pada table. Penulisan field harus sama persis dengan field pada table atau data yang diinputkan bertipe data berbeda.\r\nContohnya : \r\nsql = "UPDATE CUSTOMER SET NamaCust=\'" & txtnmcust.Text & "\', Alamat=\'" & txtalamat.Text & "\', NoTlp=\'" & txttlp.Text & "\'" where IdCust=\'" & txtidcust.Text & "\'" \r\ndb.Execute sql\r\nPenjelasan :\r\nTulisan berwarna pink seharusnya NmCust, bukan NamaCust', '2019061790203', 'Cek kembali penulisan pada perintah SQL, dan cocokan dengan fields pada table yang bersangkutan'),
	(23, 'Run-time error ‘-2147217900(80040E14)’ : Syntax error (missing operator) in query expression “Anita SesarRia, Kelas=’ Si ’, where nim=’2006-104-002’', 'Anda kurang memberikan tanda petik satu ( ‘ ) pada perintah SQL.\r\nKet tulisan berwarna merah : Pada penulisan perintah pada bagian field tersebut memiliki kekurangan petik satu ( ‘ )', '2019061790232', 'Tambahkan tanda petik satu ( ‘ ) pada perintah SQL untuk field yang betipe data text/string'),
	(24, 'Compile Error : Cant Find Project or Library', 'Anda mencoba menggunakan sub / fungsi yang dideklarasikan di file eksternal (via "Project" --> "Referensi"), tetapi karena beberapa alasan file itu tidak dapat ditemukan.', '20190617113851', 'Periksa Referensi Anda (melalui "Proyek" --> "Referensi") untuk melihat apakah ada item yang dicentang (yang semuanya ada di bagian atas daftar) mulai dengan "MISSING:"'),
	(25, 'Error 91: Object variable or With block variable not set', 'Kode Anda menggunakan Variabel Objek (misalnya: Formulir, Kotak Teks, atau Kelas) yang belum diatur.', '20190617113957', 'Banyak Variabel Objek dibuat untuk Anda (seperti Formulir dan kontrol di atasnya, ketika formulir memuat), tetapi yang lainnya yang Anda butuhkan untuk Mengatur sendiri.'),
	(26, 'Query timeout expired', 'Error ini terjadi karena batas waktu eksekusi command/perintah sql melebihi waktu yang telah ditentukan pada property CommandTimeOut. Biasanya ini terjadi saat anda mengeksekusi perintah SQL Query yang kompleks (besar/rumit/banyak data/inner join tabel).', '2019061885526', '1. Pintar pintar dalam mengolah sql query\r\n2. Set property CommandTimeOut pada variable SqlCommand / OledbCommand / OdbcCommand');
